#include <vector>

using namespace std;

vector<int> solve(int n, vector<int> h) {
  vector<int> ans(n);
  vector<int> prefixSum(n);
  prefixSum[0] = h[0];
  for (int i = 1; i < n; i++) {
    prefixSum[i] = prefixSum[i - 1] + h[i];
  }
  for (int i = 0; i < n; i++) {
    ans[i] = prefixSum[i] - h[i];
  }
  return ans;
}

int main() {
  int n;
  cin >> n;
  vector<int> h(n);
  for (int i = 0; i < n; i++) {
    cin >> h[i];
  }
  vector<int> ans = solve(n, h);
  for (int i = 0; i < n; i++) {
    cout << ans[i] << endl;
  }
  return 0;
}

