vector<int> findArray(vector<int>& gcdTable) {
  int n = gcdTable.size();
  vector<int> a(n);

  // Calculate the GCD of two numbers and store it in the table
  for (int i = 0; i < n; i++) {
    for (int j = i + 1; j < n; j++) {
      int gcd = __gcd(gcdTable[i], gcdTable[j]);
      gcdTable[i] = gcd;
    }
  }

  // Find the common divisor that is shared by all numbers in the table
  int gcd = gcdTable[0];
  for (int i = 1; i < n; i++) {
    if (gcdTable[i] != gcd) {
      gcd = 0;
      break;
    }
  }

  // If there is a common divisor, restore array a
  if (gcd) {
    for (int i = 0; i < n; i++) {
      a[i] = gcdTable[i] / gcd;
    }
  }

  return a;
}

