    string bLower = b.ToLower();

    // Ignore dots and pluses in the login part
    string aLogin = aLower.substr(0, aLower.find('@'));
    string bLogin = bLower.substr(0, bLower.find('@'));

    // Check if the login parts are equal
    if (aLogin != bLogin)
